import React, { useState, useEffect } from 'react'
import axios from 'axios'

const App = () => {
  const [ countries,setCountries ] = useState([])
  const [ showAll, setShowAll ] = useState(false)
  const [ searchWord, setSearchWord ]Â = useState('')
  const [ weatherData, setWeatherData ] = useState([])
  const [ city, setCity ] = useState('')

  useEffect(() => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => setCountries(response.data))
    if (city.length > 0) {
      axios
      .get('http://api.weatherstack.com/current?access_key=fb7d7aaea43fa42c0c5e2530db2d71cf&query='.concat(city))
      .then(response => setWeatherData(response.data))
    }
  }, [])

  /* const GetWeather = ({match}) => {
    useEffect(() => {
      axios
        .get('http://api.weatherstack.com/current?access_key=fb7d7aaea43fa42c0c5e2530db2d71cf&query='.concat(match.capital))
        .then(response => setWeatherData(response.data))
    }, [])
  } */

  const CitySetter = ({match}) => {
    return () => {
      setCity(match.capital)
    }
  }

  const RenderCountry = ({match}) => {
    // GetWeather({match})
    CitySetter({match})
    return(
      <div>
        <h2>{match.name}</h2>
        <p>
          capital {match.capital} <br/>
          population {match.population}
        </p>
        <h3>languages</h3>
        <ul>
          {match.languages.map(language => <li>{language.name}</li>)}
        </ul>
        <img src={match.flag} width="10%" height="10%"></img>
        <h3>Weather in {match.capital}</h3>
        <h4>temperature: </h4>{weatherData.temperature} Celsius
      </div>
    )
  }

  const renderer = () => {
    var countriesToShow = showAll
    ? countries
    : countries.filter(country => country.name.toLowerCase().includes(searchWord))
    console.log({countriesToShow})

    if (countriesToShow.length > 10) {
      return(
        <div>
          <p>Too many matches, specify another filter</p>
        </div>
      )
    } else if (countriesToShow.length === 0) {
      return(
        <div>
          <p>No matches found</p>
        </div>
      )
    } else if (countriesToShow.length === 1) {
      const match = countriesToShow[0]
      setCity(match.capital)
      return(
        RenderCountry({match})
      )
    } else {
      return(
        <div>
          {countriesToShow.map(match => <p key={match.numericCode}>{match.name}<button onClick={() => setSearchWord(match.name.toLowerCase())}>show</button></p> )}
        </div>
      )
    }
  }

  const handleChange = (event) => {
    setSearchWord(event.target.value)
  }

  return (
    <div>
      find countries <input value={searchWord} onChange={handleChange} />
      {renderer()}
    </div>

  )
}

export default App;
